import React from 'react'
import Container from 'react-bootstrap/Container';
import Tables from '../Table/Table';
import Table from 'react-bootstrap/Table'
import ar from '../../images/arRahuman.jpeg'
import { MDBContainer, MDBRating } from 'mdbreact'

function home() {
  const songsHeader=[
    "Artwork","Song","Date of Release","Artists", "Rating"
]
  const songsBio =[
      {

      "artwork":ar,
      "song":"Munbe va",
      "dor":"july 21,2009",
      "artists":"Ar Rahuman",
      "rating":""

  },
  {

      "artwork":"",
      "song":"Once upon a time",
      "dor":"May 20,2022",
      "artists":"Aniruth",
      "rating":""

      },
  {

          "artwork":"./images/arRahuman.jpeg",
          "song":"Bullet Song",
          "dor":"May 10 ,2022",
          "artists":"Devisri prasad",
          "rating":""

          },
      ]

  return (

      <Container>
        <div className="d-flex justify-content-between mt-5 mb-5"> 
        <h1 className="font-weight-bold text-secondary">Top 10 Songs</h1>
        <button className="btn btn-secondary">+ Add Song</button>
        </div>
        <Table striped bordered hover >
          <thead>
            {
              songsHeader.map((head)=>
                (


              <th>{head}</th>


                )    
      )
            } 
          </thead>
          <tbody>
            {songsBio.map((columns,index)=>(
              <tr>
              <td className='align-items-center'><img style={{width:"100px", height:"100px"}} src={columns.artwork}/></td>
              <td>{columns.song}</td>
              <td>{columns.dor}</td>
              <td>{columns.artists}</td>
              <td>{ <MDBContainer>
                       <MDBRating iconRegular />
                    </MDBContainer>
            }</td>
              </tr>
            ))}


          </tbody>
      </Table>

      </Container>


  )
}

export default home
